const response = $input.item.json.data;
const webhookData = $node["Webhook"].json.body;
const lastEventId = webhookData.lastEventId;
const lastStats = webhookData.lastStats;

if (!response) return { json: { hasUpdate: false } };

// --- 1. Parse Events ---
let smEvents = response.events || [];
// Sort: newest first (Sportmonks usually sends ordered, but let's ensure)
smEvents.sort((a, b) => b.id - a.id);

let hasNewEvent = false;
let latestEvent = null;
let uniqueId = null;
let eventDetail = null;

if (smEvents.length > 0) {
    latestEvent = smEvents[0];
    uniqueId = `sm_event_${latestEvent.id}`;

    if (!lastEventId || lastEventId !== uniqueId) {
        hasNewEvent = true;
        eventDetail = {
            type: latestEvent.type?.name || 'Event',
            detail: `${latestEvent.player_name || 'Player'} (${latestEvent.minute}')`,
            team: { name: latestEvent.team_id === response.participants[0].id ? response.participants[0].name : response.participants[1].name }
        };
    }
}

// --- 2. Parse Stats ---
let stats = response.statistics || [];
let team1 = stats.find(s => s.team_id === response.participants[0].id) || { statistics: [] };
let team2 = stats.find(s => s.team_id === response.participants[1].id) || { statistics: [] };

// Format to match API-Football structure slightly for consistency
const formattedStats = [
    { team: { id: response.participants[0].id, name: response.participants[0].name }, statistics: team1.statistics || [] },
    { team: { id: response.participants[1].id, name: response.participants[1].name }, statistics: team2.statistics || [] }
];

let hasStatsUpdate = false;
let statsDetail = "";
let updateType = "";

if (!hasNewEvent && lastStats) {
    // Check for simple stats changes (e.g. Shots, Corners)
    // Note: Sportmonks structure might differ, simplified check here
    // Assuming simple length check or specific field tracking for now
    // Real implementation would need mapping specific Type Names from Sportmonks
    hasStatsUpdate = JSON.stringify(formattedStats) !== JSON.stringify(lastStats);
    if (hasStatsUpdate) {
        updateType = 'GAME_FLOW';
        statsDetail = "Game stats updated";
    }
}

let outputType = 'STATS';
let outputData = formattedStats;
let finalHasUpdate = hasNewEvent || hasStatsUpdate;

if (hasNewEvent) {
    outputType = 'EVENT';
    outputData = eventDetail;
} else if (hasStatsUpdate) {
    outputType = 'GAME_FLOW';
    outputData = { type: 'STATS_CHANGE', detail: statsDetail };
}

return {
    json: {
        hasUpdate: finalHasUpdate,
        type: outputType,
        data: outputData,
        fullStats: formattedStats,
        timeElapsed: response.time?.minute || 0,
        uniqueId: hasNewEvent ? uniqueId : `sm_stats_${Date.now()}`,
        isFiller: !finalHasUpdate,
        ...webhookData
    }
};

