const stats = $input.item.json.response;
const lastStats = $node["Webhook"].json.body.lastStats;
const webhookData = $node["Webhook"].json.body;

// Get time from Check New Event node
const lastEventNode = $node["Check New Event"];
const lastEvent = lastEventNode ? lastEventNode.json.data : null;
const timeElapsed = lastEvent ? (lastEvent.time.elapsed + (lastEvent.time.extra || 0)) : 0;

if (!stats || stats.length < 2) return { json: { hasUpdate: false } };

function getStat(teamStats, typeName) {
    const stat = teamStats.statistics.find(s => s.type === typeName);
    return stat ? (stat.value || 0) : 0;
}

const team1 = stats[0];
const team2 = stats[1];

let updateType = "";
let updateDetail = "";
let hasUpdate = false;
let isFiller = false;
let fillerType = "";
let type = 'STATS';
let outputData = stats;

if (lastStats) {
  const lastTeam1 = lastStats[0];
  const lastTeam2 = lastStats[1];

  // 1. Corner Kicks
  const t1Corner = getStat(team1, "Corner Kicks");
  const lastT1Corner = getStat(lastTeam1, "Corner Kicks");
  const t2Corner = getStat(team2, "Corner Kicks");
  const lastT2Corner = getStat(lastTeam2, "Corner Kicks");

  if (t1Corner > lastT1Corner) {
      updateType = "CORNER";
      updateDetail = `เตะมุมของทางฝั่ง ${team1.team.name}`;
  } else if (t2Corner > lastT2Corner) {
      updateType = "CORNER";
      updateDetail = `เตะมุมของทางฝั่ง ${team2.team.name}`;
  }

  // 2. Shots on Goal
  if (!updateType) {
      const t1Shot = getStat(team1, "Shots on Goal");
      const lastT1Shot = getStat(lastTeam1, "Shots on Goal");
      const t2Shot = getStat(team2, "Shots on Goal");
      const lastT2Shot = getStat(lastTeam2, "Shots on Goal");

      if (t1Shot > lastT1Shot) {
          updateType = "SHOT";
          updateDetail = `จังหวะยิงเข้ากรอบของ ${team1.team.name}!`;
      } else if (t2Shot > lastT2Shot) {
          updateType = "SHOT";
          updateDetail = `จังหวะยิงเข้ากรอบของ ${team2.team.name}!`;
      }
  }
}

if (updateType) {
    hasUpdate = true;
    type = 'GAME_FLOW';
    outputData = { type: updateType, detail: updateDetail };
} else {
    // Logic for Filler/Analysis
    // Always consider speaking if no update (polling interval)
    isFiller = true;

    // Randomly decide between 'Game Flow' (Current possession/style) and 'History' (Deep analysis)
    // 60% Flow (Generic), 40% History
    if (Math.random() > 0.6) {
        fillerType = 'HISTORY';
    } else {
        fillerType = 'FLOW';
        type = 'GENERIC_FLOW';
        const t1Possession = getStat(team1, "Ball Possession") || "50%";
        const t2Possession = getStat(team2, "Ball Possession") || "50%";
        outputData = { 
           type: 'POSSESSION',
           detail: `${team1.team.name} (${t1Possession}) vs ${team2.team.name} (${t2Possession})`
        };
    }
}

return {
  json: {
    hasUpdate,
    isFiller,
    fillerType,
    type,
    data: outputData,
    fullStats: stats,
    timeElapsed,
    uniqueId: `stats_${Date.now()}`,
    ...webhookData
  }
};

