// === Helper to read node safely ===
function safeJson(nodeName) {
  try {
    if ($node[nodeName] && $node[nodeName].length > 0) {
        return $node[nodeName][0].json;
    }
    // Fallback for direct execution or other structures
    if ($node[nodeName] && $node[nodeName].json) {
        return $node[nodeName].json;
    }
    return undefined;
  } catch (e) {
    return undefined;
  }
}

// === Binary / Audio ===
const binaryData = items[0].binary.data;
const base64String = binaryData.data;

// === AI Text Source ===
const aiScriptJson  = safeJson("AI Script Writer");
const aiFillerJson  = safeJson("AI Filler Writer");
const aiChatJson    = safeJson("AI Chat Responder");

const aiNode = aiScriptJson || aiFillerJson || aiChatJson;

const aiText = aiNode?.choices
  ? aiNode.choices[0].message.content
  : "Audio generated";

// === Default ID & Description ===
let uniqueId = `audio_${Date.now()}`;
let description = "Commentary";

// === Inputs from Logic Nodes ===
const webhookJson     = safeJson("Webhook");
const checkChatNode   = safeJson("Check Chat Type"); // Note: Check Chat Type is IF node
const checkEventJson  = safeJson("Check New Event");
const statsJson       = safeJson("Check Stats Update");
const smJson          = safeJson("Process Sportmonks");

// === Chat Logic ===
// Check if the Webhook input itself had type='CHAT'
if (webhookJson && webhookJson.body && webhookJson.body.type === 'CHAT') {
  uniqueId = `chat_${Date.now()}`;
  description = "Viewer Reply";
}

// === Sportmonks Logic ===
else if (smJson && smJson.hasUpdate) {
  uniqueId = smJson.uniqueId;
  if (smJson.type === 'EVENT') {
      description = smJson.data.type + " - " + smJson.data.team.name;
  } else {
      description = smJson.data.detail || "Game Update";
  }
}

// === API-Football Event Logic ===
else if (checkEventJson?.hasNewEvent) {
  const eventData = checkEventJson;
  uniqueId = eventData.uniqueId;
  description =
    (eventData.data?.type || "Event") +
    " - " +
    (eventData.data?.team?.name || "");
}

// === API-Football Stats / Filler Logic ===
else if (statsJson) {
  uniqueId = statsJson.uniqueId;

  if (statsJson.fillerType === "HISTORY") {
    description = "Historical Analysis";
  } else if (
    statsJson.type === "GAME_FLOW" ||
    statsJson.type === "GENERIC_FLOW"
  ) {
    description = statsJson.data?.detail || "Game Flow";
  }
}

// === Return ===
return {
  json: {
    events: [
      {
        id: uniqueId,
        text: aiText,
        description: description,
        audioBase64: base64String,
      },
    ],
    currentStats: statsJson?.data || smJson?.data,
  },
};
